// draw mountain
function drawMountain() {
  background(220);
  fill(173, 216, 230);
  rect(0, 0, width, height);
  fill(55);
  strokeWeight(0);
  triangle(
    width / 3,
    height * 0.3,
    -0.3 * width,
    height * 1.3,
    width * 1.3,
    height * 1.3
  );
  triangle(
    (width / 5) * 4,
    height * 0.17,
    0.2 * width,
    height * 1.3,
    width * 1.7,
    height * 1.3
  );

  fill(240);
  ellipse(width * 0.2, height * 0.99, width * 2, height * 0.4);
  fill(255);
