//This code implements a basic image convolution filter, which is a fundamental operation in computer vision and a key component of convolutional neural networks. Let me explain what it does step by step:

let cat;
let filtered;
let dim = 28;

function preload() {
  cat = loadImage('cat.png');
}

let filter = [
  [-1, 1, 0],
  [-1, 1, 0],
  [-1, 1, 0]
];

function setup() {

  for (let i = 0; i < 3; i++) {
    for (let j = 0; j < 3; j++) {
      filter[i][j] = random(-1, 1);
    }
  }

  createCanvas(dim * 10 * 2, dim * 10);
  filtered = createImage(dim, dim);
  background(255);
  noSmooth();
  image(cat, 0, 0, dim * 10, dim * 10);
  cat.loadPixels();
  filtered.loadPixels();
  for (let x = 1; x < dim - 1; x++) {
    for (let y = 1; y < dim - 1; y++) {
      let rgb = convolution(cat, x, y, filter);
      let pix = index(x, y);
      filtered.pixels[pix + 0] = rgb.r;
      filtered.pixels[pix + 1] = rgb.g;
      filtered.pixels[pix + 2] = rgb.b;
      filtered.pixels[pix + 3] = 255;
    }
  }
  filtered.updatePixels();
  image(filtered, dim * 10, 0, dim * 10, dim * 10);
}

function index(x, y) {
  return (x + y * cat.width) * 4;
}


//factor is the weight from the filter matrix that's applied to each neighboring pixel. It's used to multiply each color channel:


function convolution(img, x, y, filter) {
  let sumR = 0;
  let sumG = 0;
  let sumB = 0;
  for (let i = -1; i <= 1; i++) {
    for (let j = -1; j <= 1; j++) {
      let pix = index(x + i, y + j);
      let factor = filter[j + 1][i + 1];
      sumR += img.pixels[pix + 0] * factor;
      sumG += img.pixels[pix + 1] * factor;
      sumB += img.pixels[pix + 2] * factor;
    }
  }

  return {
    r: sumR,
    g: sumG,
    b: sumB
  };
}

// Machine Learning for Creative Coding
// https://github.com/shiffman/ML-for-Creative-Coding

// Total pixels in each 28x28 image
const len = 784;

// Data
let catsData;
let trainsData;
let rainbowsData;
let allData = [];

// Neural network classifier
let classifier;

// DOM element to show results
let resultsP;

function preload() {
  // Load binary image datasets
  catsData = loadBytes("data/cats1000.bin");
  trainsData = loadBytes("data/trains1000.bin");
  rainbowsData = loadBytes("data/rainbows1000.bin");
}

function prepareData(data, label) {
  // Convert raw image data into training samples
  for (let i = 0; i < 1000; i++) {
    let record = {};
    let offset = i * len;
    let rawInputs = data.bytes.subarray(offset, offset + len);

    // Normalize pixel values (0-255) to a 0-1 range
    record.inputs = [];
    for (let j = 0; j < rawInputs.length; j++) {
      record.inputs[j] = rawInputs[j] / 255;
    }

    // Assign the corresponding label
    record.target = label;
    allData.push(record);
  }
}

function setup() {
  createCanvas(140, 140);

  // Needed as default "webgpu" is not compatible
  ml5.setBackend("webgl");

  background(255);

  // Prepare labeled training data
  prepareData(catsData, "cat");
  prepareData(rainbowsData, "rainbow");
  prepareData(trainsData, "train");

  // Options for configuring the neural network

  // Simple vanilla neural network
  // let options = {
  //   inputs: len,
  //   outputs: 3,
  //   task: "classification",
  //   debug: true,
  // };


  // Uses convolutional layers, keeping the 2D structure of the input image
  // (The data is still inputted as a flat array)
  let options = {
    inputs: [28, 28, 1],
    task: "imageClassification",
    outputs: 3,

    // Uncomment to set a custom learning rate
    // learningRate: 0.05,

    // Uncomment to define a custom convolutional network
    // layers: customLayers,

    debug: true,
  };

  classifier = new ml5.neuralNetwork(options);

  // Add training data to the model
  for (let i = 0; i < allData.length; i++) {
    classifier.addData(allData[i].inputs, [allData[i].target]);
  }

  // Train the model
  classifier.train({ epochs: 30 }, finishedTraining);

  // Create DOM elements for classification and clearing the canvas
  resultsP = createP("");
  let guessButton = createButton("classify");
  guessButton.mousePressed(classify);
  let clearButton = createButton("clear");
  clearButton.mousePressed(clearCanvas);
}

function draw() {
  // Allow user to draw on the canvas
  if (mouseIsPressed) {
    strokeWeight(8);
    stroke(0);
    line(pmouseX, pmouseY, mouseX, mouseY);
  }
}

function classify() {
  // Convert drawn image to a 28x28 grayscale input
  let inputs = [];
  let img = get();
  img.resize(28, 28);
  img.loadPixels();
  for (let i = 0; i < len; i++) {
    let bright = img.pixels[i * 4]; // Extract brightness from red channel
    inputs[i] = (255 - bright) / 255.0; // Normalize and invert
  }
  classifier.classify(inputs, gotResults);
}

function gotResults(results) {
  resultsP.html(`${results[0].label} (${floor(100 * results[0].confidence)}%)`);
}

