class Sun {
  constructor(x, y, m) {
    this.pos = createVector(x, y);
    this.mass = m;
    this.r = sqrt(this.mass) * 2;
  }

  attract(mover) {
    let force = p5.Vector.sub(this.pos, mover.pos);
    let distanceSq = constrain(force.magSq(), 100, 1000);
    let G = 5;
    let strength = (G * (this.mass * mover.mass)) / distanceSq;
    force.setMag(strength);
    mover.applyForce(force);
  }

  show() {
    normalMaterial();
    texture(sunTexture);
    push();
    sphere(109);
    pop();
  }
}

class Planet {
  constructor(x, y, m, planetTexture, distance, size, speed) {
    this.pos = createVector(x, y);
    this.vel = p5.Vector.random3D();
    this.vel.mult(5);
    this.acc = createVector(0, 0);
    this.mass = m;
    this.r = sqrt(this.mass) * 2;
    this.texture = planetTexture;
    this.distance = distance;
    this.size = size;
    this.speed = speed;
    this.angle = 0;
  }

  applyForce(force) {
    let f = p5.Vector.div(force, this.mass);
    this.acc.add(f);
  }

  update() {
    this.vel.add(this.acc);
    this.pos.add(this.vel);
    this.acc.set(0, 0);
    this.angle += this.speed;
  }

  show() {
    normalMaterial();
    texture(this.texture);

    this.orbit = createVector(this.distance, 0).rotate(this.angle);

    push();
    translate(this.orbit.x, this.orbit.y, 0);
    sphere(this.size);
    pop();
  }
}

class Moon {
  constructor(mass, size, distance, speed, mtext) {

    this.mass = mass;
    this.r = sqrt(this.mass) * 2;
    this.texture = mtext;
    this.distance = distance;
    this.size = size;
    this.speed = speed;
    this.angle = 0;
  }

  update(planet) {
    this.angle += this.speed;
  }

  show(planet) {
    push();

    translate(planet.orbit.x, planet.orbit.y, 0);
    let orbitDistance = this.distance * planet.size + planet.size;
    let moonOrbit = createVector(orbitDistance, 0).rotate(this.angle);
    translate(moonOrbit.x, moonOrbit.y, 0);

    normalMaterial();
    texture(this.texture);
    sphere(this.size);
    pop();
  }
}

